note_models.css:
.card {
    font-family: 'SF Pro Display', -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif;
    font-size: 18px;
    line-height: 1.6;
    color: #2c3e50;
    background: linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%);
    text-align: left;
    padding: 20px;
    border-radius: 12px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.1);
    max-width: 100%;
    margin: 0 auto;
}

/* Mobile-first responsive design */
@media (max-width: 480px) {
    .card {
        font-size: 16px;
        padding: 15px;
        margin: 10px;
    }
}

/* Typography hierarchy */
h1, h2, h3 {
    color: #34495e;
    margin-top: 0;
    font-weight: 600;
}

h2 {
    font-size: 20px;
    color: #3498db;
    border-bottom: 2px solid #3498db;
    padding-bottom: 5px;
}

h3 {
    font-size: 18px;
    color: #e74c3c;
}

/* Color coding system */
.concept { color: #3498db; font-weight: 600; }
.implementation { color: #27ae60; }
.formula { color: #f39c12; font-weight: 700; }
.edge-case { color: #e74c3c; }
.interview-tip { color: #9b59b6; font-style: italic; }

/* Code styling for mobile */
pre, code {
    font-family: 'SF Mono', 'Monaco', 'Consolas', monospace;
    font-size: 14px;
    background: #2c3e50;
    color: #ecf0f1;
    padding: 12px;
    border-radius: 8px;
    overflow-x: auto;
    white-space: pre-wrap;
    word-wrap: break-word;
    line-height: 1.4;
    margin: 10px 0;
}

@media (max-width: 480px) {
    pre, code {
        font-size: 13px;
        padding: 8px;
    }
}

/* Inline code */
code {
    display: inline;
    padding: 2px 6px;
    background: #34495e;
    border-radius: 4px;
}

/* Metadata styling */
.metadata {
    margin-top: 20px;
    padding-top: 15px;
    border-top: 1px solid #bdc3c7;
    font-size: 12px;
    color: #7f8c8d;
    text-align: center;
}

.topic {
    background: #3498db;
    color: white;
    padding: 4px 8px;
    border-radius: 4px;
    font-weight: 600;
}

.type {
    background: #95a5a6;
    color: white;
    padding: 4px 8px;
    border-radius: 4px;
    margin-left: 8px;
}

/* Visual emphasis */
strong, b {
    color: #2c3e50;
    font-weight: 700;
}

em, i {
    color: #7f8c8d;
    font-style: italic;
}

/* Interactive elements */
details {
    margin: 10px 0;
    padding: 10px;
    background: rgba(52, 152, 219, 0.1);
    border-radius: 6px;
    border-left: 4px solid #3498db;
}

summary {
    cursor: pointer;
    font-weight: 600;
    color: #3498db;
    outline: none;
}

/* Lists */
ul, ol {
    padding-left: 20px;
}

li {
    margin-bottom: 8px;
}

/* Focus on readability */
p {
    margin-bottom: 15px;
}

/* Answer separator */
hr#answer {
    border: none;
    height: 2px;
    background: linear-gradient(90deg, #3498db, #9b59b6);
    margin: 20px 0;
    border-radius: 1px;
}
