note_models.css:
.card {
 font-family: 'Segoe UI', 'Roboto', Arial, sans-serif;
 font-size: 18px;
 text-align: center;
 color: #2c3e50;
 background-color: #fdfdfd;
 line-height: 1.5;
}

.front {
 font-weight: 600;
 color: #2c3e50;
 font-size: 20px;
 padding: 15px;
}

.back {
 text-align: left;
 padding: 25px;
 max-width: 800px;
 margin: 0 auto;
}

.concept {
 color: #e74c3c;
 margin-bottom: 12px;
 font-size: 16px;
}

.concept strong {
 font-weight: 500;
}

.intuition {
 color: #3498db;
 font-style: italic;
 margin-bottom: 12px;
 font-size: 16px;
}

.intuition strong {
 font-weight: 500;
 font-style: normal;
}

.mechanics {
 color: #27ae60;
 margin-bottom: 12px;
 font-size: 16px;
}

.mechanics strong {
 font-weight: 500;
}

.tradeoffs {
 color: #e67e22;
 margin-bottom: 12px;
 font-size: 16px;
}

.tradeoffs strong {
 font-weight: 500;
}

.applications {
 color: #8e44ad;
 margin-bottom: 12px;
 font-size: 16px;
}

.applications strong {
 font-weight: 500;
}

.memory-hook {
 background-color: #f8f9fa;
 padding: 15px;
 border-radius: 6px;
 border-left: 4px solid #6c757d;
 font-style: italic;
 color: #495057;
 margin-top: 15px;
 font-size: 15px;
}

.memory-hook strong {
 font-weight: 500;
 font-style: normal;
 color: #343a40;
}

@media (max-width: 768px) {
 .back {
   padding: 20px 15px;
 }

 .card {
   font-size: 16px;
 }

 .front {
   font-size: 18px;
   padding: 12px;
 }
}
