note_models.css:
.card {
 font-family: arial;
 font-size: 20px;
 text-align: center;
 color: black;
 background-color: white;
}

.front {
 font-weight: bold;
 color: #2c3e50;
}

.back {
 text-align: left;
 padding: 20px;
}

.concept {
 font-weight: bold;
 color: #e74c3c;
 margin-bottom: 10px;
}

.intuition {
 color: #3498db;
 font-style: italic;
 margin-bottom: 10px;
}

.mechanics {
 color: #27ae60;
 margin-bottom: 10px;
}

.tradeoffs {
 color: #f39c12;
 margin-bottom: 10px;
}

.applications {
 color: #9b59b6;
 margin-bottom: 10px;
}

.memory-hook {
 background-color: #ecf0f1;
 padding: 10px;
 border-left: 4px solid #34495e;
 font-style: italic;
 color: #34495e;
}
